import React from 'react';
import { AppBar, Toolbar, Typography, Button, Box } from '@mui/material';

const Header = ({ onAddCardClick }) => {
  return (
    <AppBar position="static" color="transparent" elevation={0}>
      <Toolbar sx={{ flexWrap: 'wrap' }}> {/* Wrap toolbar content for smaller screens */}
        <Box flexGrow={1} sx={{ textAlign: { xs: 'center', md: 'left' }, marginBottom: { xs: '1rem', md: '0' } }}>
          <Typography 
            variant="h6" 
            sx={{ 
              fontWeight: 'bold',
              fontSize: { xs: '1.2rem', sm: '1.5rem', md: '1.75rem' },  // Adjust font size based on screen size
            }}
          >
            Abstract | Help Center
          </Typography>
        </Box>
        <Box 
          sx={{ 
            display: 'flex', 
            flexDirection: { xs: 'column', sm: 'row' },  // Stack buttons vertically on extra small screens
            justifyContent: { xs: 'center', md: 'flex-end' }, 
            width: { xs: '100%', md: 'auto' },
            alignItems: { xs: 'center', sm: 'inherit' }, // Align center on smaller screens
          }}
        >
          <Button
            variant="contained"
            sx={{ 
              backgroundColor: '#000', 
              color: '#fff', 
              marginBottom: { xs: '0.5rem', sm: '0' }, // Margin adjustments for smaller screens
              marginRight: { sm: '10px' },
              width: { xs: '90%', sm: 'auto' },  // Full width on extra small screens
              '&:hover': { backgroundColor: '#333' }
            }}
            onClick={onAddCardClick}  // Trigger Add Card Overlay
          >
            Add Card
          </Button>
          <Button
            variant="contained"
            sx={{ 
              backgroundColor: '#000', 
              color: '#fff', 
              width: { xs: '90%', sm: 'auto' },  // Full width on extra small screens
              '&:hover': { backgroundColor: '#333' }
            }}
          >
            Submit a request
          </Button>
        </Box>
      </Toolbar>
    </AppBar>
  );
};

export default Header;
